package week1.day1;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=10;
		System.out.println("Add two numbers:"+(a+b));
		System.out.println("Sub two numbers:"+(a-b));
		System.out.println("Multiply two numbers:"+(a*b));
		System.out.println("Div two numbers:"+(a/b));

		
		System.out.println("check for equals:"+(a==b));
		System.out.println("check for greatest between two:"+(a>b));
		System.out.println("check for lowest between two:"+(a<b));
	}

}
